import sqlite3

#creates three tables - cancer_types, genetic_mutations, and treatment
conn = sqlite3.connect("main.db")
cur=conn.cursor()
cur.execute("CREATE TABLE IF NOT EXISTS cancer_types (Cancer_Name text PRIMARY KEY, Affected_Tissue text, Gene_Name text)")
cur.execute("CREATE TABLE IF NOT EXISTS genetic_mutations (Gene_Name text PRIMARY KEY, Gene_Sequence text, Protein text)")
cur.execute("CREATE TABLE IF NOT EXISTS treatment (Drug_Name text PRIMARY KEY, Gene_Name text, Function text)")
conn.commit()
conn.close()


#populate cancer_types table
def insertC(Cancer_Name, Affected_Tissue, Gene_Name):
    conn=sqlite3.connect("main.db")
    cur=conn.cursor()
    cur.execute("INSERT INTO cancer_types VALUES (?,?,?)",(Cancer_Name, Affected_Tissue, Gene_Name))
    conn.commit()
    conn.close()
    viewC()

#populates genetic_mutations table 
def insertG(Gene_Name, Gene_Sequence, Protein):
    conn=sqlite3.connect("main.db")
    cur=conn.cursor()
    cur.execute("INSERT INTO genetic_mutations VALUES (?,?,?)",(Gene_Name,Gene_Sequence,Protein))
    conn.commit()
    conn.close()
    viewG()

#populate treatment table 
def insertT(Drug_Name,Gene_Name,Function):
    conn=sqlite3.connect("main.db")
    cur=conn.cursor()
    cur.execute("INSERT INTO treatment VALUES (?,?,?)",(Drug_Name,Gene_Name,Function))
    conn.commit()
    conn.close()
    viewT()

#Change the select statement to choose the info you want to see, then run view()
#use when you want to see all the rows in either table
def viewC():
    conn=sqlite3.connect("main.db")
    cur=conn.cursor()
    cur.execute("SELECT * FROM cancer_types")
    rows=cur.fetchall()
    conn.close()
    print(rows)

def viewG():
    conn=sqlite3.connect("main.db")
    cur=conn.cursor()
    cur.execute("SELECT * FROM genetic_mutations")
    rows=cur.fetchall()
    conn.close()
    print(rows)

def viewT():
    conn=sqlite3.connect("main.db")
    cur=conn.cursor()
    cur.execute("SELECT * FROM treatment")
    rows=cur.fetchall()
    conn.close()
    print(rows)

